		<h1 class="mb-0 font-weight-bold">FUN</h1>
		<div class="container-fluid pt-2 pr-4 pl-lg-4">
			<div class="row">
				<div v-for="(activity, index) in activities" v-bind:key="activity.image"
				     class="col-6 col-sm-4 col-md-3 px-3 px-md-3 px-lg-4 py-4">
					<div v-bind:id="`Activity${index}`" class="position-relative rounded-circle image-wrapper"
					     v-on:click="activateModal(activity, index)">
						<img v-bind:src="require(`../assets/${activity.image}`)" v-bind:alt="activity.captions"
						     class="position-absolute w-100 h-100 rounded-circle">
					</div>
					<h2 class="text-white text-center font-weight-bold mt-4 mb-0">{{activity.captions}}</h2>
				</div>
			</div>
		</div>
		<b-modal id="ImageModal" centered hide-header hide-footer
		         v-model="showModal"
		         v-bind:return-focus="triggerElementId"
		         v-bind:size="modalSize"
		         v-bind:body-class="'p-0'">
			<div class="position-relative w-100 h-100 modal-wrapper">
				<img v-bind:src="require(`../assets/${modalContent.image}`)" v-bind:alt="modalContent.captions"
				     class="w-100 h-100">
				<button type="button" v-on:click="showModal = false"
				        class="position-absolute border-0 rounded-circle d-flex justify-content-center align-items-center"
				        style="width: 36px; height: 36px; top: 6px; right: 6px; background-color: rgba(0,0,0,.5)">
					<font-awesome-icon v-bind:icon="['fas', 'times']"
					                   style="font-size: 1.5rem; color: white"></font-awesome-icon>
				</button>
			</div>
		</b-modal>
	</div>
</template>

<script>
import { BModal } from "bootstrap-vue"

export default {
	name: "SectionFun",
	
	components: {
		BModal,
	},
	
	mounted() {
		const imageWrappers = this.$el.querySelectorAll(".image-wrapper");
		const observer = new IntersectionObserver((entries, observer) => {
			entries.forEach(({ target, isIntersecting }) => {
				isIntersecting ? target.classList.add("active") : target.classList.remove("active");
			})
		}, { threshold: .25 });
		imageWrappers.forEach(image => observer.observe(image));
	},
	
	data() {
		return {
			showModal: false,
			modalSize: "md",
			triggerElementId: "Activity0",
