		activateModal(activity, index) {
			this.showModal = true;
			this.modalContent = activity;
			this.triggerElementId = `#Activity${index}`;
			const innerWidth = window.innerWidth;
			if (innerWidth > 992) {
				this.modalSize = "xl";
			} else if (innerWidth > 768) {
				this.modalSize = "lg";
			} else if (innerWidth > 576) {
				this.modalSize = "md";
			} else {
				this.modalSize = "sm";
			}
		}
	},
